<?php

namespace Octolize\Shipping\CanadaPost\Beacon;

/**
 * Can display Help Scout beacon.
 */
class Beacon extends \OctolizeShippingCanadaPostVendor\WPDesk\Beacon\Beacon {

	/**
	 * Beacon constructor.
	 *
	 * @param BeaconDisplayStrategy $strategy .
	 * @param string                $assets_url .
	 */
	public function __construct( BeaconDisplayStrategy $strategy, $assets_url ) {
		parent::__construct(
			'32950b6e-e141-4318-8409-1daf4231461d',
			$strategy,
			$assets_url,
			'hs-beacon-search',
			'data:image/png;base64,'
		);
		$this->confirmation_message = __( 'When you click OK, our HelpScout beacon will open where you can start a conversation with us. The beacon loads the chat form and also potentially sets cookies.', 'octolize-canada-post-shipping' );
	}

}
